import { ChatService } from './../../services/chat.service';
import { SocketService } from './../../services/socket.service';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit {

  text = "";
   constructor(public chat: ChatService){}
  ngOnInit(): void {

}


sendMessage(){
let messageinfo = {
  text: this.text,
  messageType: 1
};
this.chat.sendMessage(messageinfo);
this.text = '';
}
}

<div class="chatContainer">
  <p ngClass="{'local': message.messageType === 1, 'remoto': message.messageType === 2}" class="local" *ngFor="let message of chat?.chats">{{message.text}}</p>
  <p ngClass="{'local': message.messageType === 1, 'remoto': message.messageType === 2}" class="remoto" *ngFor="let message of chat?.chats">{{message.text}}</p>

</div>

<div class="inputContainer">
  <form (submit)="sendMessage()">
    <input [(ngModel)]="text" [ngModelOptions]="{ standalone: true }" type="text">  </form>
</div>
